import React from 'react'
import Navbar from './Navbar';
import { HeroImage, heroFrame } from '../../constant/images';



const Hero = () => {
  return (
    <div 
      style={{ backgroundImage: `linear-gradient(to right, rgba(30, 13, 55, 0.7), rgba(128, 0, 128, 0.7)), url(${HeroImage})`, 
      backgroundSize: 'cover', 
      backgroundRepeat: 'no-repeat', 
      backgroundColor: '#000080' }} 
      className={`w-full h-[850px]`}>
      <Navbar/>

      <div className="flex max-w-[1200px] w-full mx-auto h-full gap-[40px]">
        <div className="flex flex-2 flex-col">
          <h1 className='font-[600] text-[]'>Africa's all-in-one booking, payment & business management solution</h1>
          <p>All-in-one booking software to grow your business in Africa. Book appointments, manage clients, get paid - all in one place.</p>
          <div className="flex items-center gap-6"></div>
        </div>

        <div className="flex-1 flex items-baseline justify-end bg-White2 h-max">
          <img src={heroFrame} alt="" className="mt-auto " />
        </div>

      </div>
    </div>
  )
}

export default Hero;
